import axios from "axios";
import React, { useEffect, useState } from "react";

const DeleteMultiple = () => {
    const [users, setUsers] = useState([]);
    useEffect(() => {
        setTimeout(() => {
            axios.get("http://localhost:3006/user")
                .then((res) => {
                    res.data && setUsers(res.data)
                })
                .catch((err) => {
                    console.log(err);
                })
        }, 3000)
    }, [])

    const handleChange = (e) => {
        const { name, checked } = e.target;
        if (name === "allselect") {
            const checkedValue = users.map((user) => { return { ...user, isChecked: checked } });
            console.log(checkedValue);
            setUsers(checkedValue)
        }
        else {
            const checkedValue = users.map((user) => user.username === name ? { ...user, isChecked: checked } : user)
            console.log(checkedValue);
            setUsers(checkedValue)
        }
    }

    const handleAllDelete = () => {
        const checkedInputValue = [];
        for (let i = 0; i < users.length; i++) {
            if (users[i].isChecked === true) {
                checkedInputValue.push(parseInt(users[i].userid));
            }
        }
        axios.delete(`http://localhost:3006/user/${checkedInputValue}`)
            .then((data) => {
                setUsers(data);
            })
            .catch((err) => alert(err))
        //console.log(JSON.stringify(checkedInputValue));
    }

    return (
        <>
            <div className="content">
                <div className="row">
                    <div className="col-sm-12">
                        <h2 className="mt-4 mb-4 fw-bold">
                            Delete Multiple row data in Reacjs
                        </h2>
                        <button className="btn btn-danger mb-3" onClick={() => handleAllDelete()}>All Delete</button>
                        <form className="form w-100">
                            <table className="table">
                                <thead>
                                    <tr>
                                        <th>
                                            <input type="checkbox" name="allselect" checked={!users.some((user) => user?.isChecked !== true)} onChange={(e) => handleChange(e)}></input>
                                        </th>
                                        <th>
                                            Sr.No
                                        </th>
                                        <th>
                                            User Name
                                        </th>
                                        <th>
                                            Email
                                        </th>
                                        <th>
                                            Address
                                        </th>
                                        <th>
                                            Action
                                        </th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        users && users.map((user, index) => {
                                            return (
                                                <tr key={user.userid}>
                                                    <th>
                                                        <input type="checkbox" name={user.username} checked={user?.isChecked || false} onChange={(e) => handleChange(e)} />
                                                    </th>
                                                    <td>{index + 1}</td>
                                                    <td>{user.username}</td>
                                                    <td>{user.useremail}</td>
                                                    <td>{user.useradress}</td>
                                                    <td>
                                                        <button className="btn btn-danger">Delete</button>
                                                    </td>
                                                </tr>
                                            )
                                        })
                                    }
                                </tbody>
                            </table>
                        </form>
                    </div>
                </div>
            </div>
        </>
    )
}

export default DeleteMultiple;
